insert into teacher(tcode, name, password, gender, degree, title, introduction, photo) values('003719', '邓小方', '123456', '男', '硕士', '讲师', '邓小方，男，汉族，1980年4月生，2005年7月参加工作，2006年加入中国共产党，中山大学信息科学与计算学院软件与理论专业毕业，研究生学历，工学硕士学位。现任江西师范大学软件学院教师，主讲课程包括《商务软件数据管理与维护》、《WEB应用程序设计》、《线性代数》等。指导学生获得“安利杯”计算机软件大赛全国铜奖1次、江西省电子电脑设计大赛一等奖若干次。', '~\images\teacher\003719.jpg');
insert into teacher(tcode, name, password, gender, degree, title, introduction, photo) values('003720', '胡国雄', '123456', '男', '硕士', '讲师', '胡国雄，1977年生。南京航空航天大学在读博士研究生导航、制导与控制专业模式识别研究方向。职称讲师。一直在江西师范大学软件学院从事教学科研工作。近年来主要主讲《嵌入式实时操作系统》、《嵌入式数据库技术》、《需求工程》、《ARM体系结构与编程》等课程。发表SCI、EI、CSSCI等论文10多篇，主持省级和校级课题多项。', '~\images\teacher\003720.jpg');
insert into teacher(tcode, name, password, gender, degree, title, introduction, photo) values('003721', '黄龙军', '123456', '男', '硕士', '副教授', '黄龙军，男，副教授，先后担任10余门主干课程教学，教学方法和教学效果得到了广大师生的一致肯定和高度评价，07年获得校课堂竞赛二等奖。积极指导学生参加课外科技竞赛，取得省级一等奖三项，国家三等奖一项。在搞好教学的同时，积极拓展思维，寻找学术方向和研究领域的突破点，先后完成或在研省级课题十项（主持完成一项，在研两项），其中“交互式社会保险决策支持系统”获于2008年省科技进步二等奖（排名第三）。共发表论文40篇，其中第一作者18篇（EI检索8篇，ISTP检索2篇，核心期刊7篇）。另外承担了软件学院的科研干事， 09年获得学校优秀科研干事称号；参与学院一系列质量工程项目。积极参与学位点的建设工作，包括培养方案修订、硕士学位点申报、冲博等各项工作。', '~\images\teacher\003721.jpg');
insert into teacher(tcode, name, password, gender, degree, title, introduction, photo) values('003722', '章志明', '123456', '男', '硕士', '副教授', '章志明，硕士研究生，副教授。2013年获江西师范大学“三育人”先进工作者称号。近年来主要承担了软件学院C语言程序设计、Java语言程序设计和算法与数据结构等专业课程的教学工作；承担了软件学院J2EE和移动终端软件开发工作室的教学与上机辅导工作。作为骨干成员参与完成多项省、校级教改课题；指导学生完成校实验室开放项目、大学生学术课题5项；自2012年来，指导学生获国家级大赛三等奖3项，获省级大赛一等奖2项，获省级大赛三等奖10余项。主要研究方向为无线传感器网络安全、电子商务安全、信息系统分析、软件项目管理。', '~\images\teacher\003722.jpg');
